export class Device {
  static filterDevices(names, devices) {
    if (names == null || devices == null) {
      return [];
    }
    const ret = [];
    for (const name of names) {
      const dev = devices.find(d => d.name === name);
      if (dev != null) {
        ret.push(dev);
      }
    }
    return ret;
  }
  constructor(name, displayname) {
    this.name = name;
    this.displayname = displayname;
  }
  getName() {
    return this.name;
  }
  getDisplayName() {
    return this.displayname;
  }
}
export class Input extends Device {
  static getInput(name, inputs) {
    for (const i of inputs) {
      if (i.name === name) {
        return i;
      }
      if (i.subInputs !== undefined && i.subInputs.length > 0) {
        for (const sub of i.subInputs) {
          if (sub.name === name) {
            return sub;
          }
        }
      }
    }
  }
  constructor(name, displayname, icon, reachableDisplays, subs) {
    super(name, displayname);
    this.click = new EventEmitter();
    this.reachableDisplays = [];
    this.subInputs = [];
    this.icon = icon;
    this.reachableDisplays = reachableDisplays;
    this.subInputs = subs;
  }
  isDisplayReachable(name) {
    return this.reachableDisplays.includes(name);
  }
  getIcon() {
    return this.icon;
  }
}
export class Output extends Device {
  static getPower(outputs) {
    for (const o of outputs) {
      if (o.power === "on") {
        return o.power;
      }
    }
    return "standby";
  }
  static getInput(outputs) {
    let input = null;
    for (const o of outputs) {
      if (input == null) {
        input = o.input;
      } else if (o.input !== input) {
        // this means the input that appears selected may not actually be selected on all displays.
        // to get the ~correct~ behavior, return null.
        return o.input;
      }
    }
    return input;
  }
  static setPower(s, outputs) {
    outputs.forEach(o => o.power = s);
  }
  static setInput(i, outputs) {
    outputs.forEach(o => o.input = i);
  }
  constructor(name, displayname, power, input) {
    super(name, displayname);
    this.power = power;
    this.input = input;
    this.powerEmitter = new EventEmitter();
  }
}
